if (! usingInterrupt) {
// read data from the GPS in the 'main loop'
char c = {{varname}}.read();
// if you want to debug, this is a good time to do it!
if (GPSECHO)
if (c) Serial.print(c);
}
// if a sentence is received, we can check the checksum, parse it...
if ({{varname}}.newNMEAreceived()) {
// a tricky thing here is if we print the NMEA sentence, or data
// we end up not listening and catching other sentences!
// so be very wary if using OUTPUT_ALLDATA and trytng to print out data
//Serial.println({{varname}}.lastNMEA());   // this also sets the newNMEAreceived() flag to false
if (!{{varname}}.parse({{varname}}.lastNMEA()))   // this also sets the newNMEAreceived() flag to false
return;  // we can fail to parse a sentence in which case we should just wait for another
}
// if millis() or timer wraps around, we'll just reset it
if (timer > millis())  timer = millis();
// approximately every 2 seconds or so, print out the current stats
if (millis() - timer > 2000) {
timer = millis(); // reset the timer
Serial.print("\nTime: ");
Serial.print({{varname}}.hour, DEC); Serial.print(':');
Serial.print({{varname}}.minute, DEC); Serial.print(':');
Serial.print({{varname}}.seconds, DEC); Serial.print('.');
Serial.println({{varname}}.milliseconds);
Serial.print("Date: ");
Serial.print({{varname}}.day, DEC); Serial.print('/');
Serial.print({{varname}}.month, DEC); Serial.print("/20");
Serial.println({{varname}}.year, DEC);
Serial.print("Fix: "); Serial.print((int){{varname}}.fix);
Serial.print(" quality: "); Serial.println((int){{varname}}.fixquality);
if ({{varname}}.fix) {
Serial.print("Location: ");
Serial.print({{varname}}.latitude, 4); Serial.print({{varname}}.lat);
Serial.print(", ");
Serial.print({{varname}}.longitude, 4); Serial.println({{varname}}.lon);
Serial.print("Location (in degrees, works with Google Maps): ");
Serial.print({{varname}}.latitudeDegrees, 4);
Serial.print(", ");
Serial.println({{varname}}.longitudeDegrees, 4);
Serial.print("Speed (knots): "); Serial.println({{varname}}.speed);
Serial.print("Angle: "); Serial.println({{varname}}.angle);
Serial.print("Altitude: "); Serial.println({{varname}}.altitude);
Serial.print("Satellites: "); Serial.println((int){{varname}}.satellites);
}
}
