for(int i=0 ; i <= {{varname}}_NUMOFLEDS ; i++){
for (int k = 0 ; k <= 255 ; k = k + 5) {
// set leds Color to RGB values, from 0,0,0 up to 255,255,255
{{varname}}.setPixelColor(i, {{varname}}.Color(255-k,k,100)); // turn on green color on led #i.
if (i > 0)
{{varname}}.setPixelColor(i-1, {{varname}}.Color(0,0,0)); // turn off last led
{{varname}}.show(); //update led color to the hardware.
delay(1);
}
}
